package chapter03;

public class CastingOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch1 = 'A';
		char ch2 = 'Z';
		
		int n1 = ch1;
		int n2 = ch2;
		
		System.out.println(n1);
		System.out.println(n2);
		
		long l1 = 100000000000l;
		float f1 = l1;
		
		System.out.println(f1);

	}

}
